import { useState, useEffect, useContext } from 'react';

import styled from 'styled-components';

import Barbell from '../Barbell';
import testimoniesData from './testimoniesData';
import TitleBanner from '../titleBanner';
import TestimoniesCard from './TestimoniesCard';
import Carousel from '@brainhubeu/react-carousel';

const Styled = styled.section`
  padding: 5em 1em;
  text-align: center;

  .barbell {
    margin: 0;
    margin-top: 1em;
  }

  .container {
    display: flex;
  }

  .card--tesimonies {
    flex-shrink: 1;
  }

  .BrainhubCarousel__trackContainer {
    margin-top: 5em;
    ul {
      display: flex;
      flex-wrap: wrap;
    }

    li {
      flex: 45%;
    }
  }
  .barbell {
    max-width: 500px;
    margin: 0 auto;
  }
`;

const Testimonies = () => {
  return (
    <Styled className='testimoneis'>
      <TitleBanner title='testimonies' subtitle='what our memebers say' />
      <Barbell />
      <Carousel
        slidesPerPage={3}
        infinite
        centered
        autoPlay={5000}
        animationSpeed={2000}
      >
        {testimoniesData.map(item => (
          <TestimoniesCard key={item.id} data={item} />
        ))}
      </Carousel>
    </Styled>
  );
};

export default Testimonies;
