import styled from 'styled-components';
import Button from './button';
import Fade from 'react-reveal/Fade';
import { Children } from 'react';

const Styled = styled.div`
  padding: 0 0.5em;
  .subtitle {
    font-family: var(--fn-reenie);
    font-size: 1.5rem;
    margin-bottom: 1em;
  }
  .title-img {
    margin-top: 5em;
    margin-bottom: 1em;
  }

  @media screen and (min-width: 768px) {
    .title-img {
      max-width: 450px;
    }
  }
  @media screen and (min-width: 1024px) {
    .subtitle {
      font-size: 1.7rem;
    }

    .button {
      font-size: 1.15rem;
    }
  }
`;

const Banner = ({ banner, bannerTitle }) => {
  return (
    <Styled>
      <Fade bottom>
        <h1 className='title'>{bannerTitle}</h1>
        {banner === 'home' ? (
          <Fade top delay={500}>
            <Button title='visit us' toLink='/contact' />
          </Fade>
        ) : (
          <Fade top delay={500}>
            <div className='btn--group'>
              <Button title='visit us' toLink='/contact' />
              <Button title='visit us' toLink='/contact' />
            </div>
          </Fade>
        )}
      </Fade>
    </Styled>
  );
};

// <Fade top delay={500}>
//   <Button title='visit us' toLink='/contact' />
// </Fade>

export default Banner;
