import styled from 'styled-components';
import Barbell from '../Barbell';
import TitleBanner from '../titleBanner';

const Styled = styled.section`
  .card--testimoneis {
    position: relative;
    background-color: var(--accent-clr);
    color: var(--second-clr);
    text-align: center;
    padding: 5em 1em 2em;
  }

  .card--testimoneis--img {
    border-radius: 50%;
    height: 120px;
    width: 120px;
    margin: 0 auto;
    border: 3px solid var(--primary-clr);
    object-fit: cover;
    position: absolute;
    top: -50px;
    left: 0;
    right: 0;
  }
`;

const Testimonies = () => {
  return (
    <Styled className='testimoneis'>
      <TitleBanner title='testimonies' subtitle='what our memebers say' />
      <Barbell />
      <div className='container'>
        <div className='card--testimoneis'>
          <img
            src='woman_two.jpg'
            alt='woman'
            className='card--testimoneis--img'
          />
          <div className='card--testimoneis--content'>
            <h3 className='card--testimoneis--content__name'>Alex Smioth</h3>
            <p className='card--testimoneis--content__para'>
              Lorem ipsum dolor sit amet, consectetur adipisc ulum porttitor
              egestas orci, vinec at velit vestibulum, Lorem ipsum dolor sit
              amet, consectetur adipisc ulum porttitor egestas orci, vinec at
              velit vestibulum,
            </p>
          </div>
        </div>
      </div>
    </Styled>
  );
};

export default Testimonies;
