import styled from 'styled-components';

const Styled = styled.div`
  position: relative;
  width: 220px;
  height: 400px;
  border: 2px solid var(--primary-clr);
  display: grid;
  justify-items: center;
  /* padding: 8em 1em; */

  .card--title {
    color: ${props =>
      props.invert ? 'var(--second-clr)' : 'var(--white-clr)'};
    font-weight: bold;
    align-self: center;
    font-size: 1.7rem;

    .month {
      display: block;
      color: ${props =>
        props.invert ? 'var(--second-clr)' : 'var(--white-clr)'};
      font-weight: normal;
      font-size: 1rem;
    }
  }

  .card--packages {
    /* padding: 0.5em 1em 1.5em; */
    color: var(--second-clr);
    font-weight: bold;
    text-transform: uppercase;
    text-align: center;

    &__item {
      color: ${props =>
        props.invert ? 'var(--second-clr)' : 'var(--white-clr)'};
    }
  }
`;

const MembershipCard = ({ data: { id, price, included } }) => (
  <Styled className='card--membership'>
    <h3 className='card--title'>
      ${price} <span className='month'>/month</span>
    </h3>
    <ul className='card--packages'>
      {included.map((item, i) => (
        <li key={i} className='card--packages__item'>
          {item}
        </li>
      ))}
    </ul>
  </Styled>
);

export default MembershipCard;
