import { useState, useEffect } from 'react';
import StateContext from './StateContext';

const StateProvider = ({ children }) => {
  const [windowSize, setWindowSize] = useState(0);

  const setCarosuel = () => {
    if (windowSize < 768) {
      return 1;
    } else if (windowSize >= 768) {
      return 3;
    } else if (windowSize >= 1280) {
      return 4;
    }
  };

