import styled from 'styled-components';
import TitleBanner from '../titleBanner';
import Button from '../button';
import services from './servicesData';
import Card from '../Card';

const Stlyed = styled.section`
  position: relative;
  background: url(bars.jpg);
  background-size: cover;
  background-position: left;
  object-fit: cover;
  min-height: 100vh;
  padding: 5em 2em;
  z-index: 1;
  text-align: center;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-color: ${props => props.heroOpacity || 'rgba(0, 0, 0, 0.65)'};
    z-index: -1;
  }

  .card--container {
    margin-top: 3em;
  }

  .card {
    position: relative;
    max-width: 350px;
    margin: 4em auto;

    &::before {
      content: '';
      position: absolute;
      background-color: var(--primary-clr);
      height: 100%;
      width: 100%;
      top: 12px;
      /* left: 0; */
      right: -6px;
      z-index: -1;
    }

    &--img {
      position: relative;
      transition: var(--ease--in--out--02s);
    }
    &--body {
      padding: 0.5em 1em 1.5em;
      background-color: var(--white-clr);
      color: var(--second-clr);

      &__para {
        padding-top: 0.5em;
      }
    }
  }

  @media screen and (min-width: 760px) {
    .card--container {
      display: grid;
      grid-template-columns: repeat(3, 1fr);
      grid-gap: 1em;
      justify-content: center;
    }
  }
`;

const Services = () => {
  return (
    <Stlyed>
      <TitleBanner title='Services' subtitle='STEP UP YOUR FITNESS' invert />
      <div className='card--container'>
        {services.map(card => (
          <Card key={card.id} data={card} />
        ))}
      </div>
      <Button title='view more' toLink='/services' />
    </Stlyed>
  );
};

export default Services;
