import { useState } from 'react';
import styled from 'styled-components';

import Button from './button';
import Fade from 'react-reveal/Fade';
import useForm from '../Hooks/useForm';

const Styled = styled.section`
  position: relative;
  background-image: url(/platesbg.jpg);
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  object-fit: cover;
  min-height: 75vw;
  z-index: 1;
  display: grid;
  justify-items: center;
  align-content: center;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-color: rgba(0, 0, 0, 0.65);
    z-index: -1;
  }

  .title--content {
    padding: 0 1em;
    text-align: center;
  }
  .img-line {
    width: 100px;
  }
`;

const ScheduleWeek = () => {
  const { handleChange, handleSubmit, form, setForm } = useForm(
    {
      name: '',
      email: '',
      phone: ''
    },
    submit
  );

  const submit = () => {
    console.log(form);
  };

  return (
    <Styled className='schedule--weeek'>
      <div className='title--content'>
        <h2 className='title'>SCHEDULE YOUR FREE WEEK</h2>
        <img src='/yellowLine.png' alt='yellow line' className='img-line' />
      </div>
      <form className='btns--container'>
        <input
          type='text'
          className='input--tab'
          placeholder='NAME'
          onChange={handleChange}
          name='name'
          value={form.name}
        />
        <input
          type='email'
          className='input--tab'
          placeholder='EMAIL'
          onChange={handleChange}
          name='email'
          value={form.email}
        />
        <input
          type='number'
          className='input--tab'
          placeholder='PHONE'
          onChange={handleChange}
          name='phone'
          value={form.phone}
        />
        <input type='submit' value='SUBMIT' className='input--tab last--tab' />
      </form>
    </Styled>
  );
};

export default ScheduleWeek;
