const Footer = () => (
  <footer>
    <div className='content'>
      <div className='addresse'>
        <img className='logo' src='/logo.png' alt='logo' />
        <p className='info'>203 Fake St, chicago, Ill</p>
        <p className='info'>(33) 333-3333</p>
      </div>
      <div className='hours'>
        <p>Open 24/7</p>
      </div>
      <Socials />
    </div>
    <div className='copyRight'>
      <p>Â© Fit On 2020</p>
    </div>

    <style jsx>
      {`
        footer {
          margin-top: auto;
          display: grid;
          position: relative;
          text-align: center;
          background-color: var(--second-clr);
          background-size: cover;
          background-repeat: no-repeat;
          object-fit: cover;
          background-position: center;
          z-index: 1;
        }

        p {
          color: var(--white-clr);
        }

        .content {
          display: grid;
          grid-gap: 1em;
          gap: 1em;
          padding: 2em 0;
          max-width: 1400px;
          margin: 0 auto;
        }

        .addresse {
          display: grid;
          grid-gap: 0.5em;
          gap: 0.5em;
          justify-content: center;
        }

        .logo {
          width: 150px;
          margin: 0 auto 1em;
        }

        .hours {
          display: grid;
          grid-gap: 0.3em;
          gap: 0.3em;
          justify-content: center;
          align-items: center;
        }

        .copyRight {
          background-color: black;
          padding: 1em 0;
          align-self: end;
        }

        @media screen and (min-width: 1024px) {
          .content {
            grid-template-columns: repeat(3, 1fr);
            padding: 3em 0;
          }

          .social {
            order: -1;
            width: 250px;
          }

          .social--link {
            font-size: 1.7rem;
          }
        }
      `}
    </style>
  </footer>
);

export default Footer;
