  padding: 3em 5vw 0;
  text-align: center;
  display: grid;
  gap: 4em;
  justify-items: center;

  .title--banner {
    margin-bottom: 1.3em;
  }

  .para {
    margin-bottom: 1em;
  }

  .button {
    margin-top: 1em;
  }

  @media screen and (min-width: 760px) {
    grid-template-columns: repeat(2, 1fr);
    gap: 1em;

    .content {
      order: 1;
    }

    .imgs--container {
      display: grid;
      grid-template-columns: repeat(2, 1fr);
    }

    .img {
      height: 100%;
      object-fit: cover;
    }
  }
  @media screen and (min-width: 1024px) {
  }
`;

const About = () => {
  return (
    <Styled className='home--about'>
      <Fade>
        <div className='content'>
          <TitleBanner title='welcome' subtitle='why choose us?' />
