import styled from 'styled-components';
import Fade from 'react-reveal/Fade';
import TitleBanner from '../ui/titleBanner';
import Button from '../ui/button';

const Styled = styled.section`
  padding: 3em 5vw 4em;
  text-align: center;
  display: grid;
  grid-gap: 4em;
  justify-items: center;

  .title--banner {
    margin-bottom: 1.3em;
  }

  .para {
    margin-bottom: 1em;
  }

  .button {
    margin-top: 1em;
  }

  .imgs--container {
    position: relative;
    position: relative;
    transition: var(--ease--in--out--02s);
    width: 100%;
    img {
      object-fit: cover;
      height: 450px;
    }

    &::before {
      content: '';
      position: absolute;
      background-color: var(--primary-clr);
      height: 100%;
      width: 100%;
      top: 12px;
      right: -6px;
      z-index: -1;
    }
  }

  @media screen and (min-width: 760px) {
    grid-template-columns: repeat(2, 1fr);
    grid-gap: 1em;

    .content {
      order: 1;
      display: grid;

      & > :last-child {
        align-self: self-end;
      }
    }

    .imgs--container {
    }
  }
  @media screen and (min-width: 1200px) {
    grid-gap: 5em;
    text-align: left;

    .title--banner,
    .title {
      margin: 0;
    }
  }
`;
const AboutContent = () => (
