import { useRouter } from 'next/router';
import Link from 'next/link';
import trainers from '../../components/ui/meetOurTeam/trainers';
import styled from 'styled-components';

const Styled = styled.section``;

const Trainer = ({ trainer }) => {
  const router = useRouter();
  const { id } = router.query;

  console.log(trainer);
  return (
    <Styled className='trainer'>
      <div className='imgs--container'>
        <img src={trainer.img} alt={trainer.title} />
      </div>
      <div className='content'>
        <h1 className='name'>
          {trainer.title}
          <span className='subtitle'>{trainer.subtitle}</span>
        </h1>
        <p className='para'>{trainer.text}</p>
        <Fade top delay={500}>
          <Button title='back' toLink={useRouter.back()} invert />
        </Fade>
      </div>
    </Styled>
  );
};

Trainer.getInitialProps = async ctx => {
  const trainer = trainers.filter(item => item.id === ctx.query.id);
  return { trainer };
};

export default Trainer;
