import styled from 'styled-components';
import Barbell from '../ui/Barbell';
import TitleBanner from '../ui/titleBanner';
import MembershipCard from '../ui/MembershipCard';
import memerships from '../ui/membershipPlans/memeberships';

const Styled = styled.section`
  padding: 4em 1em;

  .card--container {
    max-width: 600px;
    margin: 4em auto;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    grid-gap: 3em;

    & > :last-child {
      grid-column: auto;
    }
  }

  @media screen and (min-width: 1200px) {
    .card--container {
      max-width: 900px;
      grid-gap: 0.1em;
    }
  }

  @media screen and (min-width: 768px) {
    & > :first-child {
      width: 90%;
      margin: 0 auto;
    }
  }
  @media screen and (min-width: 1280px) {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    justify-content: center;

    & > :first-child {
      align-self: end;
      margin: 0;
    }

    .title--banner {
      margin-left: 9em;
    }

    .card--container {
      grid-column: 1 / 3;
    }
  }
`;

const PricingContent = () => {
  return (
    <Styled classname='pricing--content max-width'>
      <Barbell />
      <TitleBanner title='pricing' subtitle='membership plans' />
      <div className='card--container'>
        {memerships.map((items, i) => (
          <MembershipCard key={i} data={items} invert />
        ))}
      </div>
    </Styled>
  );
};

export default PricingContent;
