import styled from 'styled-components';
import Button from './button';

const Styled = styled.div`
  position: relative;
  width: 180px;
  height: 400px;
  border: 2px solid var(--primary-clr);
  display: grid;
  padding: 0 1em;
  justify-items: center;
  /* padding: 8em 1em; */

  .card--title {
    color: ${props =>
      props.invert ? 'var(--second-clr)' : 'var(--white-clr)'};
    font-weight: bold;
    align-self: center;
    font-size: 1.75rem;

    .month {
      display: block;
      color: ${props =>
        props.invert ? 'var(--second-clr)' : 'var(--white-clr)'};
      font-weight: normal;
      font-size: 1rem;
    }
  }

  .card--packages {
    /* padding: 0.5em 1em 1.5em; */
    color: var(--second-clr);
    font-weight: bold;
    text-transform: uppercase;
    text-align: center;

    &__item {
      color: ${props =>
        props.invert ? 'var(--second-clr)' : 'var(--white-clr)'};
      text-align: center;
      margin-bottom: 1.5em;
    }

    .line {
      width: 50px;
      height: 9px;
      background-color: var(--primary-clr);
      margin: 0 auto;
    }
  }
`;

const MembershipCard = ({ data: { id, price, included } }) => (
  <Styled className='card--membership'>
    <h3 className='card--title'>
      ${price} <span className='month'>/month</span>
    </h3>
    <ul className='card--packages'>
      {included.map((item, i) => (
        <li key={i} className='card--packages__item'>
          {item === '-' ? <div className='line' /> : item}
        </li>
      ))}
    </ul>
    <Button title='cc' />
  </Styled>
);

export default MembershipCard;
