import styled from 'styled-components';

const Styled = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  text-transform: uppercase;

  &::after,
  &::before {
    content: '';
    position: absolute;
    height: 10px;
    width: 10px;
    background-color: red;
    top: 50%;
    left: 0;
    transform: translate(-50%, -50%);
  }

  &::after {
    background-image: url(rightTitle.png);
    background-size: cover;
    right: 0;
  }

  .title {
    color: var(--primary-clr);
    font-weight: var(--fw-bold);
    font-size: 2.2rem;
    -webkit-text-stroke: 2px var(--primary-clr);
  }

  .subtitle {
    color: var(--second-clr);
    -webkit-text-stroke: 0.5px var(--second-clr);
    /* font-weight: bold; */
  }
`;

const TitleBanner = ({ subtitle, title }) => (
  <Styled className='title--banner'>
    <h2 className='title'>{title}</h2>
    <h3 className='title'>{subtitle}</h3>
  </Styled>
);

export default TitleBanner;
