import styled from 'styled-components';

const Styled = styled.div`
  /* display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  text-transform: uppercase; */
  position: relative;
  max-width: fit-content;
  margin: 0 auto;

  .title,
  .subtitle {
    position: relative;
    width: 100%;
    display: block;
    font-family: var(--fn-monterrat);
    font-weight: var(--fw-bold);
    text-transform: uppercase;
  }

  .title {
    color: var(--primary-clr);
    -webkit-text-stroke: 2px var(--primary-clr);
    &::after,
    &::before {
      content: '';
      position: absolute;
      height: 30px;
      width: 30px;
      top: 0;
      bottom: 0;
      transform: translate(50%, 50%);
      background-size: cover;
    }

    &::before {
      background-image: url(leftTitle.png);
      /* left: 0; */
    }

    &::after {
      background-image: url(rightTitle.png);
      /* right: 0; */
    }
  }

  .subtitle {
    color: var(--second-clr);
    -webkit-text-stroke: 0.5px var(--second-clr);
  }
`;

const TitleBanner = ({ subtitle, title }) => (
  <Styled className='title--banner'>
    <h2 className='title'>{title}</h2>
    <h3 className='subtitle'>{subtitle}</h3>
  </Styled>
);

export default TitleBanner;
