import { useRouter } from 'next/router';
import trainers from '../../components/ui/meetOurTeam/trainers';
import styled from 'styled-components';
import Button from '../../components/ui/button';
import Fade from 'react-reveal';
import Layout from '../../components/layout';
import Banner from '../../components/ui/banner';

const Styled = styled.section``;

const Trainer = ({ trainer }) => {
  const router = useRouter();
  //   const { id } = router.query;

  console.log(trainer);
  return (
    <Layout img='/secondBg.jpg' heroSize='calc(100vh - 40vh)'>
      <Banner bannerTitle='about' btnTwo='classes' />
      <Styled className='trainer'>
        <div className='imgs--container'>
          <img src={trainer.img} alt={trainer.title} />
        </div>
        <div className='content'>
          <h1 className='name'>
            {trainer.title}
            <span className='subtitle'>{trainer.subtitle}</span>
          </h1>
          <p className='para'>{trainer.text}</p>
          <Fade top delay={500}>
            <Button title='back' toLink='/team' invert />
          </Fade>
        </div>
      </Styled>
    </Layout>
  );
};

Trainer.getInitialProps = async ctx => {
  const trainer = trainers.filter(item => item.id === ctx.query.id);
  return { trainer };
};

export default Trainer;
