import styled from 'styled-components';
import Fade from 'react-reveal/Fade';
import Button from './button';

const Styled = styled.section`
  position: relative;
  background-image: url(/platesbg.jpg);
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  object-fit: cover;
  min-height: 75vw;
  z-index: 1;
  display: grid;
  justify-items: center;
  align-content: center;
  padding: 2em;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-color: rgba(0, 0, 0, 0.75);
    z-index: -1;
  }

  .title--content {
    padding: 0 1em;
    text-align: center;
  }

  .title {
      span {
          color: var(--primary-clr);
      }
  }


  .img-line {
    width: 100px;
    margin: 0 auto;
  }


  /* .input--tab {
    display: block;
    font-family: var(--fn-monterrat);
    font-weight: var(--fw-bold);
    font-size: 1em;
    text-align: center;
    background-color: var(--white-clr);
    color: var(--second-clr);
    padding: 1em 1em;
    border-radius: 40px;
    border: 1px solid var(--second-clr);
    position: relative;
    transition: var(--ease--in--out--02s);
    cursor: pointer;
    margin-bottom: 1em;

    &:focus {
      outline: none;
      border-color: var(--primary-clr);
      border: 2px solid var(--primary-clr);
      color: ${props => props.hoverclr && 'var(--second-clr)'};
      cursor: pointer;
    }
  } */

  .last--tab {
    background-color: var(--primary-clr);

    &:focus,
    &:hover {
      border-color: var(--primary-clr);
      border: 2px solid var(--primary-clr);
      color: var(--white-clr);
      background-color: transparent;
    }
  }

  @media screen and (min-width: 760px) {
    min-height: 20vw;

  }
  @media screen and (min-width: 1100px) {
  }
`;

const PersonalTrainerBanner = () => (
  <Styled className='schedule--weeek'>
    <div className='title--content'>
      <h2 className='title'>
        Do you need a <span>Personal Trainer?</span>
        Get in touch with us now.
      </h2>
      <img src='/yellowLine.png' alt='yellow line' className='img-line' />
    </div>
    <Button title='contact us' toLink='/contact' />
  </Styled>
);

export default PersonalTrainerBanner;
