import { useRouter } from 'next/router';
import trainers from '../../components/ui/meetOurTeam/trainers';
import styled from 'styled-components';
import Button from '../../components/ui/button';
import Fade from 'react-reveal';
import Layout from '../../components/layout';
import Banner from '../../components/ui/banner';
import Hero from '../../components/ui/hero';

const Styled = styled.section`
  padding: 5em 1em;
  text-align: center;

  .imgs--container {
    position: relative;
    position: relative;
    transition: var(--ease--in--out--02s);
    max-width: 440px;
    width: 100%;
    height: 500px;

    img {
      object-fit: cover;
      height: 500px;
    }

    &::before {
      content: '';
      position: absolute;
      background-color: var(--primary-clr);
      height: 100%;
      width: 100%;
      top: -30px;
      left: -10px;
      z-index: -1;
    }
  }
  .name {
    font-weight: bold;
    text-transform: uppercase;
    text-align: center;
    margin-top: 1em;
    color: var(--primary-clr);

    span {
      color: var(--second-clr);
      font-size: 1.2rem;
    }
  }
`;

const Trainer = ({ trainer }) => {
  const router = useRouter();
  //   const { id } = router.query;

  return (
    <Layout>
      <Hero img='/secondBg.jpg' heroSize='calc(100vh - 40vh)'>
        <Banner bannerTitle='trainer' btnTwo='classes' />
      </Hero>
      <Styled className='trainer'>
        <div className='imgs--container'>
          <img src={trainer[0].img} alt={trainer[0].title} />
        </div>
        <div className='content'>
          <h1 className='name'>
            {trainer[0].title}
            <span className='subtitle'>{trainer[0].subtitle}</span>
          </h1>
          <p className='para'>{trainer[0].text}</p>
          <Fade top delay={500}>
            <Button title='back' toLink='/team' invert />
          </Fade>
        </div>
      </Styled>
    </Layout>
  );
};

Trainer.getInitialProps = async ctx => {
  const trainer = trainers.filter(item => item.id === ctx.query.id);
  return { trainer };
};

export default Trainer;
